import React, { useEffect, useState } from 'react'

import {loadTweets} from "../lookup"

export function TweetsList(props){
    const [tweets, setTweets] = useState([])
  
    useEffect(() => {
      const myCallback = (response, status) => {
        if (status === 200){
          setTweets(response)
        } else {
          alert("There was an error")
        }
      }
      loadTweets(myCallback)
    }, [])
    return tweets.map((item, index)=>{
      return <Tweet tweet={item} className='my-5 py-5 border bg-white text-dark' key={`${index}-{item.id}`}/>
    })
  }

export function ActionBtn(props) {
    const {tweet, action} = props
    const [likes, setLikes] = tweet.likes ? tweet.likes : 0
    const className = props.className ? props.className : 'btn btn-primary'
    const actionDisplay = action.display ? action.display : "Action"
    const handleClick = (event) => {
      event.preventDefault()
      if (action.type = "like") {
        console.log(tweet.likes + 1)
      }
    }
    const display = action.type === "like" ? `${tweet.likes} ${action.display}` : actionDisplay
    return <button className={className} onClick={handleClick}>{display}</button>
  }
  
export function Tweet(props) {
    const {tweet} = props
    const className = props.className ? props.className : 'col-10 mx-auto col-md-6'
    const like = {type:"like", display:"Likes"}
    const unlike = {type:"unlike", display:"Unlike"}
    const retweet = {type:"retweet", display:"Retweet"}
    return <div className={className}>
        <p>{tweet.id} - {tweet.content}</p>
        <div className = 'btn btn-group'>
          <ActionBtn tweet={tweet} action={like}/>
          <ActionBtn tweet={tweet} action={unlike}/>
          <ActionBtn tweet={tweet} action={retweet}/>
        </div>
    </div>
  }
